mapper.select:
select s.SALARY_ID, s.STATUS, s.DATE, s.SEND_TIME, s.SALARY_AMOUNT, s.NET_SALARY, s.ALLOWANCE1_AMOUNT, s.ALLOWANCE2_AMOUNT, s.DEDUCTION1_AMOUNT, s.DEDUCTION2_AMOUNT, s.TOTAL_ALLOWANCE, s.TOTAL_DEDUCTION, s.USER_ID, u.USERNAME, d.NAME as DEPARTMENTNAME, u.BIANMA
from s left join u on s.USER_ID = u.USER_ID left join d on u.DEPARTMENT_ID = d.DEPARTMENT_ID
where s.USER_ID = u.USER_ID
and u.USERNAME = #{pd.USERNAME}
and ( s.STATUS LIKE '%'+#{pd.keywords}+'%' or s.SALARY_AMOUNT LIKE '%'+#{pd.keywords}+'%' or u.USERNAME LIKE '%'+#{pd.keywords}+'%' or u.BIANMA LIKE '%'+#{pd.keywords}+'%'
)
and s.STATUS = #{pd.STATUS}
and s.DATE >= #{pd.DATE}

mapper.delete:
delete from where SALARY_ID in
#{item}
and SALARY_ID <> '1'
